rule.Check:
study.versions@$sv.
  (
    $sv.**.*[scopeId and text and instanceType]
      {
        $join([text,scopeId,instanceType],"\n"):
          (
            $i:=$;
            $count($i) > 1 ? $i.
              {
                "instanceType": instanceType,
                "id": id,
                "path": _path,
                "text": text,
                "scopeId": scopeId,
                "Organization.name": ($si:=scopeId;$sv.organizations[id=$si].name),
                "type.decode": type.decode
              }
          )
      }
  ).*
